import React, {useState, useEffect} from 'react';
import { Button, Row, Col, Jumbotron } from 'react-bootstrap';
import '../TextArea/textarea.css';

function TextArea(props) {
    const [currentText, setCurrentText] = useState(props.currentText);
    const [userText, setUserText] = useState(props.userText.split(''));

    useEffect(() => {
        setUserText(props.userText);
    })

  return (

       <Row>
           <Col xl={12}>
           <Jumbotron>
               {
                   userText.map((s,i) => {
                       let color;
                       if (i < userText.length){
                        color = s === props.userInput[i] ? '#dfffa0' : '#fcbea4';
                       }
                       return <span key={i} style={{backgroundColor: color}}>{s}</span>
                   })
               }
                {currentText} <br />
                {userText}
           </Jumbotron>
           </Col>
       </Row>

  );
}

export default TextArea;
